declare global {
  interface Array<T> {
    groupBy(fn: (item: T) => string): Record<string, T[]>;
  }
}

Array.prototype.groupBy = function (fn) {
  const o: Record<string, any> = {};
  this.forEach(item => {
    let arr = o[fn(item)];
    if (!arr) arr = o[fn(item)] = [];
    arr.push(item);
  });
  return o;
};
