class Solution {
    public:
        int maxDivScore(vector<int>& nums, vector<int>& divisors) {
            int res = 0x7fffffff, resCnt = 0;
            for (auto &v : divisors) {
                int cnt = 0;
                for (auto &num : nums) {
                    if (num % v == 0) cnt++;
                }
                if (resCnt < cnt || resCnt == cnt && res > v) {
                    res = v;
                    resCnt = cnt;
                }
            }
            return res;
        }
    };

class Solution:
    def maxDivScore(self, nums: List[int], divisors: List[int]) -> int:
        res = 0x7fffffff
        resCnt = 0
        for v in divisors:
            cnt = 0
            for num in nums:
                if num % v == 0:
                    cnt += 1
            if resCnt < cnt or resCnt == cnt and res > v:
                res = v
                resCnt = cnt
        return res

impl Solution {
    pub fn max_div_score(nums: Vec<i32>, divisors: Vec<i32>) -> i32 {
        let mut res = i32::MAX;
        let mut resCnt = 0;
        for v in divisors {
            let mut cnt = 0;
            for num in nums.iter() {
                if *num % v == 0 {
                    cnt += 1;
                }
            }
            if resCnt < cnt || resCnt == cnt && res > v {
                res = v;
                resCnt = cnt;
            }
        }
        res
    }
}

class Solution:
    def maxDivScore(self, nums: List[int], divisors: List[int]) -> int:
        res = 0
        res_num = divisors[0]
        arr = [sum(num % divisor == 0 for num in nums) for divisor in divisors]
        for i in range(len(divisors)):
            if arr[i] >= res:
                if arr[i] > res or arr[i] == res and divisors[i] < res_num:
                    res_num = divisors[i]
                res = arr[i]
        return res_num
