class Solution:
    def sumOfMultiples(self, n: int) -> int:
        return sum(
            list(
                filter(
                    lambda num: num % 3 == 0 or num % 5 == 0 or num % 7 == 0, 
                    [num for num in range(1, n + 1)]
                )
            )
        )
