class Solution:
    def isWinner(self, player1: List[int], player2: List[int]) -> int:
        def getScore(player: List[int]) -> int:
            cur = 0
            sum = 0
            for v in player:
                sum += v + v * ((cur & 0b11) != 0)
                cur = cur << 1 | (v == 10)
            return sum
        s1, s2 = getScore(player1), getScore(player2)
        return 1 if s1 > s2 else 2 if s2 > s1 else 0

class Solution {
public:
    int getScore(vector<int>& player) {
        int cur = 0, sum = 0;
        for (auto &v: player) {
            sum += v + v * ((cur & 0b11) != 0);
            cur = cur << 1 | (v == 10);
        }
        return sum;
    }
    int isWinner(vector<int>& player1, vector<int>& player2) {
        int s1 = getScore(player1), s2 = getScore(player2);
        return s1 > s2 ? 1 : s2 > s1 ? 2 : 0;
    }
};

impl Solution {
    pub fn is_winner(player1: Vec<i32>, player2: Vec<i32>) -> i32 {
        fn getScore(player: &Vec<i32>) -> i32 {
            let mut cur = 0;
            let mut sum = 0;
            for v in player {
                sum += *v + *v * ((cur & 0b11) != 0) as i32;
                cur = cur << 1 | (*v == 10) as i32;
            }
            sum
        }
        let (s1, s2) = (getScore(&player1), getScore(&player2));
        if s1 > s2 {
            1
        } else if s2 > s1 {
            2
        } else {
            0
        }
    }
}
