class Solution {
public:
    vector<int> distinctDifferenceArray(vector<int>& nums) {
        unordered_set<int> s;
        int n = nums.size();
        vector<int> list1(n, 0), list2(n, 0), res(n, 0);
        for (int i = 0; i < n; i++) {
            s.insert(nums[i]);
            list1[i] = s.size();
        }
        s.clear();
        for (int i = n - 1; i >= 0; i--) {
            list2[i] = s.size();
            s.insert(nums[i]);
        }
        for (int i = 0; i < n; i++) {
            res[i] = list1[i] - list2[i];
        }
        return res;
    }
};

impl Solution {
    pub fn distinct_difference_array(nums: Vec<i32>) -> Vec<i32> {
        let mut s = std::collections::HashSet::<i32>::new();
        let n = nums.len();
        let mut list1 = vec![0; n];
        let mut list2 = vec![0; n];
        let mut res = vec![0; n];
        for i in 0..n {
            s.insert(nums[i]);
            list1[i] = s.len();
        }
        s.clear();
        for i in (0..n).rev() {
            list2[i] = s.len();
            s.insert(nums[i]);
        }
        for i in 0..n {
            res[i] = (list1[i] - list2[i]) as i32
        }
        res
    }
}
