declare global {
    interface Function {
        callPolyfill(context: Record<any, any>, ...args: any[]): any;
    }
}
Function.prototype.callPolyfill = function (context: any, ...args): any {
    const temp = Symbol();
    context.__proto__[temp] = this;
    const res =  context[temp](...args);
    delete context.__proto__[temp];
    return res;
};
