class Solution:
    def smallestString(self, s: str) -> str:
        arr = list(s)
        starti = 0
        while starti < len(arr) and arr[starti] == 'a':
            starti += 1
        if starti == len(arr):
            arr[-1] = 'z'
        else:
            endi = starti + 1
            while endi < len(arr) and arr[endi] != 'a': endi += 1
            for i in range(starti, endi):
                arr[i] = chr(ord(arr[i]) - 1)
        return ''.join(arr)
