class Solution:
    def minCost(self, nums: List[int], x: int) -> int:
        n = len(nums)
        dp = [[inf] * n for _ in range(n + 1)]
        for i in range(n): dp[0][i] = nums[i]
        res = sum(nums)
        for offset in range(1, n):
            for i in range(n):
                dp[offset][i] = min(dp[offset - 1][i], nums[(i + offset) % n])
            res = min(res, sum(dp[offset]) + x * offset)
        return res
