class Solution {
public:
    int distanceTraveled(int mainTank, int additionalTank) {
        int used = 0, res = 0;
        while (mainTank) {
            used++;
            mainTank--;
            res += 10;
            if (used % 5 == 0 && additionalTank) {
                mainTank++;
                additionalTank--;
            }
        }
        return res;
    }
};

class Solution:
    def distanceTraveled(self, mainTank: int, additionalTank: int) -> int:
        used = res = 0
        while mainTank:
            used+=1
            mainTank-=1
            res+=10
            if used % 5 == 0 and additionalTank:
                mainTank+=1
                additionalTank-=1
        return res

impl Solution {
    pub fn distance_traveled(mut main_tank: i32, mut additional_tank: i32) -> i32 {
        let mut used = 0;
        let mut res = 0;
        while main_tank != 0 {
            used += 1;
            main_tank -= 1;
            res += 10;
            if used % 5 == 0 && additional_tank != 0 {
                main_tank += 1;
                additional_tank -= 1;
            }
        }
        res
    }
}

class Solution:
    def distanceTraveled(self, mainTank: int, additionalTank: int) -> int:
        res = 0
        while mainTank >= 5:
            res += 5 * 10
            mainTank -= 5
            if additionalTank:
                mainTank += 1
                additionalTank -= 1
        return res + mainTank * 10
