class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        n = len(nums)
        @cache
        def dfs(last: int, mask: int) -> int:
            if mask == (1 << n) - 1: return 1
            return sum(
                dfs(nums[i], mask | (1 << i))
                for i in range(n)
                if mask & (1 << i) == 0 and (last % nums[i] == 0 or nums[i] % last == 0)
            )
        return sum(dfs(nums[i], 1 << i) for i in range(n)) % (10 ** 9 + 7)
