class Solution:
    def paintWalls(self, cost: List[int], time: List[int]) -> int:
        n = len(cost)
        @cache
        def dfs(idx: int, cur_time: int) -> int:
            if cur_time >= n - idx: return 0
            if idx == n: return inf
            return min(
                dfs(idx + 1, cur_time + time[idx]) + cost[idx],
                dfs(idx + 1, cur_time - 1)
            )
        return dfs(0, 0)
