class Solution:
    def paintWalls(self, cost: List[int], time: List[int]) -> int:
        n = len(cost)
        @cache
        def dfs(idx: int, cnt: int):
            if cnt >= n - idx: return 0
            if idx == n: return inf
            return min(dfs(idx + 1, cnt + time[idx]) + cost[idx], dfs(idx + 1, cnt - 1))
        return dfs(0, 0)
