class Solution:
    def maximumBeauty(self, nums: List[int], k: int) -> int:
        base = min(nums) - k
        arr = [0] * (max(nums) + k + 2 - base)
        for num in nums:
            arr[num - k - base] += 1
            arr[num + k + 1 - base] -= 1
        for i in range(1, len(arr)): arr[i] += arr[i - 1]
        return max(arr)
