class Solution:
    def maxScore(self, nums: List[int], x: int) -> int:
        prev = [0, 0]
        prev[nums[0] & 1] = nums[0]
        prev[nums[0] & 1 ^ 1] = nums[0] - x
        for num in nums[1:]:
            v = max(prev[num & 1] + num, prev[num & 1 ^ 1] + num - x)
            prev[num & 1] = max(prev[num & 1], v)
        return max(prev)
