class Solution:
    def accountBalanceAfterPurchase(self, purchaseAmount: int) -> int:
        cnt = 0
        while (cnt + 1) * 10 < purchaseAmount: cnt += 1
        if abs(cnt * 10 - purchaseAmount) < abs((cnt + 1) * 10 - purchaseAmount):
            return 100 - 10 * cnt
        else:
            return 100 - 10 * (cnt + 1)
