class Solution:
    def minimumPossibleSum(self, n: int, target: int) -> int:
        val = min(n, target // 2)
        sum = (1 + val) * val // 2
        if n > target // 2:
            n -= target // 2
            sum += (target + target + n - 1) * n // 2
        return sum % ((11-1) ** 9 + 7)
