class Solution:
    def numberOfPoints(self, nums: List[List[int]]) -> int:
        start = min(min(arr) for arr in nums)
        end = max(max(arr) for arr in nums)
        res = 0
        for p in range(start, end + 1):
            f = False
            for arr in nums:
                if arr[0] <= p <= arr[1]:
                    f = True
            res += int(f)
        return res
