class Solution:
    def minimumSum(self, nums: List[int]) -> int:
        ans = inf
        for k in range(len(nums)):
            for j in range(k):
                for i in range(j):
                    if nums[i] < nums[j] > nums[k]:
                        ans = min(ans, nums[i] + nums[j] + nums[k])
        return ans if ans != inf else -1
