class Solution:
    def minimumAddedCoins(self, coins: List[int], target: int) -> int:
        coins.sort()
        ans = 0
        idx = 0
        end = 1 # [0, end - 1]
        while end - 1 < target:
            if idx < len(coins) and coins[idx] <= end:
                end += coins[idx]
                idx += 1
            else:
                end += end
                ans += 1
        return ans
