class Solution:
    def countTestedDevices(self, batteryPercentages: List[int]) -> int:
        res = 0
        for i in range(len(batteryPercentages)):
            if batteryPercentages[i] > 0:
                for j in range(i + 1, len(batteryPercentages)):
                    batteryPercentages[j] -= 1
                res += 1
        return res
