class Solution:
    def maximumLength(self, s: str) -> int:
        n = len(s)
        i = 0
        counter = Counter()
        while i < n:
            j = i
            while i < n and s[j] == s[i]: i += 1
            for cnt in range(1, i - j + 1):
                counter[s[j] * cnt] += i - j - cnt + 1
        return max([len(item[0]) for item in counter.items() if item[1] >= 3], default = -1)
