class Solution:
    def canSortArray(self, nums: List[int]) -> bool:
        get = lambda num: bin(num).count('1')
        res = []
        cnt1 = -1
        arr = []
        for num in nums:
            if cnt1 != get(num):
                cnt1 = get(num)
                res += sorted(arr)
                arr.clear()
            arr.append(num)
        res += sorted(arr)
        return all(res[i] <= res[i + 1] for i in range(len(res) - 1))
