class Solution:
    def minimumLevels(self, possible: List[int]) -> int:
        bob = sum(v if v else -1 for v in possible)
        alice = 0
        for i in range(len(possible) - 1):
            v = possible[i] if possible[i] else -1
            alice += v
            bob -= v
            if alice > bob: return i + 1
        return -1
