class NumArray {
  private sums: number[] = [];
  constructor(private nums: number[]) {
    let sum = 0;
    for (let i = 0, l = nums.length; i < l; i++) {
      this.sums[i] = sum += nums[i];
    }
  }
  sumRange(i: number, j: number): number {
    return this.sums[j] - this.sums[i] + this.nums[i];
  }
}
