function minPatches(nums: number[], n: number): number {
  let patches = 0;
  let x = 1;
  const len = nums.length;
  let i = 0;
  while (x <= n) {
    if (i < len && nums[i] <= x) {
      x += nums[i];
      i++;
    } else {
      x *= 2;
      patches++;
    }
  }
  return patches;
}
