class Solution {
   public:
    bool increasingTriplet(vector<int>& nums) {
        int pre1 = INT_MAX, pre2 = INT_MAX;
        for (auto& num : nums) {
            if (num > pre2) return 1;
            if (num > pre1) pre2 = min(pre2, num);
            pre1 = min(pre1, num);
        }
        return 0;
    }
};
