function reverseVowels(s: string): string {
  const set = new Set(['a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U']);
  let l = 0;
  let r = s.length - 1;
  while (l < r) {
    if (set.has(s[r]) && set.has(s[l])) {
      s = s.substring(0, l) + s[r] + s.substring(l + 1, r) + s[l] + s.substring(r + 1);
      l++;
      r--;
    }
    if (!set.has(s[l])) l++;
    if (!set.has(s[r])) r--;
  }
  return s;
}
