function maxEnvelopes(envelopes: number[][]): number {
  const len = envelopes.length;
  if (len === 0) return 0;
  envelopes.sort(([w1], [w2]) => w1 - w2);
  const dp: number[] = [1];
  for (let i = 1; i < len; i++) {
    const [w, h] = envelopes[i];
    let max = 1;
    for (let j = 0; j < i; j++) {
      const envelope = envelopes[j];
      if (w > envelope[0] && h > envelope[1]) max = Math.max(dp[j] + 1, max);
    }
    dp[i] = max;
  }
  return Math.max(...dp);
}
