function isPerfectSquare(num: number): boolean {
  const s = Math.sqrt(num);
  return s === Math.floor(s);
}

function isPerfectSquare(num: number): boolean {
  if (num === 1) return true;
  let l = 1,
    r = num / 2;
  while (l <= r) {
    const mid = (l + r) >> 1;
    const midNum = mid ** 2;
    if (midNum === num) return true;
    if (midNum > num) r = mid - 1;
    else l = mid + 1;
  }
  return false;
}
