class Solution {
   public:
    vector<int> arr;
    Solution(ListNode* head) {
        srand(time(0));
        ListNode* p = head;
        while (p) {
            arr.push_back(p->val);
            p = p->next;
        }
    }

    int getRandom() { return arr[rand() % arr.size()]; }
};

class Solution {
   public:
    ListNode *node;
    Solution(ListNode *head) {
        srand(time(0));
        node = head;
    }

    int getRandom() {
        int ans, i = 1;
        for (ListNode *p = node; p; p = p->next, i++) {
            if (rand() % i == 0) ans = p->val;
        }
        return ans;
    }
};
