function canConstruct(ransomNote: string, magazine: string): boolean {
  const map: Record<string, number> = {};
  for (const c of magazine) map[c] = (map[c] ?? 0) + 1;
  for (const c of ransomNote) {
    if (!map[c]) return false;
    map[c]--;
  }
  return true;
}

bool canConstruct(char * ransomNote, char * magazine){
    int ans[26] = {0};
    for (int i = 0; i < strlen(magazine); i++) ans[magazine[i] - 'a']++;
    for (int i = 0; i < strlen(ransomNote); i++) {
        if (!ans[ransomNote[i] - 'a']) return 0;
        ans[ransomNote[i] - 'a']--;
    }
    return 1;
}

class Solution:
    def canConstruct(self, ransomNote: str, magazine: str) -> bool:
        map = Counter(magazine)
        for c in ransomNote:
            map[c] -= 1
            if map[c] < 0: return False
        return True

fn gcd(a: i32, b: i32) -> i32 {
    if a < b {
        gcd(b, a)
    } else if b == 0 {
        a
    } else {
        gcd(b, a % b)
    }
}
impl Solution {
    pub fn insert_greatest_common_divisors(head: Option<Box<ListNode>>) -> Option<Box<ListNode>> {
        let mut head = head.unwrap();
        let mut p = &mut head;
        while let Some(mut next) = p.next.take() {
            let mut new_next = Box::new(ListNode::new(gcd(p.val, next.val)));
            new_next.next = Some(next);
            p.next = Some(new_next);
            p = p.next.as_mut().unwrap().next.as_mut().unwrap();
        }
        Some(head)
    }
}
