function firstUniqChar(s: string): number {
  const dict: Record<string, number> = {};
  for (const c of s) {
    dict[c] = (dict[c] ?? 0) + 1;
  }
  const set = new Set(
    Object.entries(dict)
      .filter(([, v]) => v === 1)
      .map(([k]) => k)
  );
  for (let i = 0, l = s.length; i < l; i++) {
    if (set.has(s[i])) return i;
  }
  return -1;
}
