class Solution {
   public:
    /*
    cnt 有多少个
    step 每个间隔多少
    start 起始值
    dir 方向
    */
    int dfs(int cnt, int step, int start, int dir) {
        if (cnt == 1) return start;
        return dfs(cnt >> 1, step << 1,
                   start + (dir == 1 || cnt & 1 ? step : 0), dir * -1);
    }
    int lastRemaining(int n) { return dfs(n, 1, 1, 1); }
};
