class Solution:
    def minRectanglesToCoverPoints(self, points: List[List[int]], w: int) -> int:
        points.sort(key = lambda p: p[0])
        res = 0
        last = -1
        for point in points:
            if point[0] <= last: continue
            res += 1
            last = point[0] + w
        return res
