class Solution:
    def numberOfRightTriangles(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])
        rows = [sum(grid[i][j] for j in range(m)) for i in range(n)]
        cols = [sum(grid[i][j] for i in range(n)) for j in range(m)]
        return sum(
            (rows[i] - 1) * (cols[j] - 1)
            for i in range(n)
            for j in range(m)
            if grid[i][j]
        )
