class Solution:
    def minimumAddedInteger(self, nums1: List[int], nums2: List[int]) -> int:
        nums1.sort()
        nums2.sort()
        n = len(nums1)
        res = inf
        for i in range(n):
            for j in range(i):
                i1 = 0
                while i1 < n and (i1 == i or i1 == j): i1 += 1
                i2 = 0
                diff = nums2[i2] - nums1[i1]
                need_skip = False
                while i1 < n:
                    if nums2[i2] - nums1[i1] != diff:
                        need_skip = True
                        break
                    i1 += 1
                    while i1 < n and (i1 == i or i1 == j): i1 += 1
                    i2 += 1
                if not need_skip:
                    res = min(res, diff)
        return res
