class Solution:
    def minEnd(self, n: int, x: int) -> int:
        nstr = list(bin(n - 1)[2:])
        xstr = list(bin(x)[2:])
        nstr.reverse()
        xstr.reverse()
        res = []
        for bit in xstr:
            if bit == '0' and nstr: res.append(nstr.pop(0))
            else: res.append(bit)
        while nstr: res.append(nstr.pop(0))
        res.reverse()
        return eval('0b' + ''.join(res))
