class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        arr = list(set(rewardValues))
        arr.sort()
        n = len(arr)
        @cache
        def dfs(idx: int, cur: int) -> int:
            if idx == n: return cur
            res = dfs(idx + 1, cur)
            if arr[idx] > cur:
                res = max(res, dfs(idx + 1, cur + arr[idx]))
            return res
        return dfs(0, 0)
