class Solution:
    def winningPlayerCount(self, n: int, pick: List[List[int]]) -> int:
        res = set()
        arr = [defaultdict(int) for _ in range(n)]
        for x, y in pick:
            arr[x][y] += 1
            if arr[x][y] > x:
                res.add(x)
        return len(res)
