class Solution:
    def resultsArray(self, nums: List[int], k: int) -> List[int]:
        if k == 1: return nums
        res = []
        prev = 0
        for i in range(1, len(nums)):
            if nums[i] != nums[i - 1] + 1:
                prev = i
            if i >= k - 1:
                if i - prev + 1 >= k:
                    res.append(nums[i])
                else:
                    res.append(-1)
        return res
