class Solution:
    def getFinalState(self, nums: List[int], k: int, multiplier: int) -> List[int]:
        q = []
        for i in range(len(nums)): heappush(q, (nums[i], i))
        while k:
            v, i = heappop(q)
            nums[i] = v * multiplier
            heappush(q, (nums[i], i))
            k -= 1
        return nums
