function sumOfLeftLeaves(root: TreeNode | null): number {
  let sum = 0;
  order(root);
  return sum;
  function order(root: TreeNode | null): void {
    if (root === null) return;
    root.left && !root.left.left && !root.left.right && (sum += root.left.val);
    order(root.left);
    order(root.right);
  }
}
