class Solution {
   public:
    int longestPalindrome(string s) {
        unordered_map<char, int> m;
        for (auto &c : s) m[c]++;
        int ans = 0, odd = 0;
        for (auto &item : m) {
            if (item.second & 1) {
                odd = 1;
                item.second -= 1;
            }
            ans += item.second;
        }
        return ans + odd;
    }
};
