class Solution {
   public:
    vector<vector<int>> levelOrder(Node *root) {
        vector<vector<int>> ans;
        if (!root) return ans;
        queue<Node *> q;
        q.push(root);
        int size = 1;
        vector<int> cur;
        while (q.size()) {
            Node *node = q.front();
            q.pop();
            cur.push_back(node->val);
            for (auto child : node->children) q.push(child);
            if (--size == 0) {
                size = q.size();
                ans.push_back(cur);
                cur.clear();
            }
        }
        return ans;
    }
};
