function countSegments(s: string): number {
  let ans = 0;
  let f = false;
  for (const c of s) {
    if (c === ' ') {
      if (f) {
        ans++;
        f = false;
      }
      f = false;
    } else {
      f = true;
    }
  }
  if (f) ans++;
  return ans;
}
