function numberOfArithmeticSlices(nums: number[]): number {
  const map = new Map<number, Map<number, number>>(
    nums.map((_, i) => [i, new Map<number, number>()])
  );
  const n = nums.length;
  let ans = 0;
  for (let i = 0; i < n; i++) {
    const num = nums[i];
    for (let j = 0; j < i; j++) {
      const v = num - nums[j];
      const c = map.get(j)!.get(v) ?? 0;
      ans += c;
      map.get(i)!.set(v, (map.get(i)!.get(v) ?? 0) + c + 1);
    }
  }
  return ans;
}
