function findMinArrowShots(points: number[][]): number {
  if (points.length === 0) return 0;
  points.sort(([, p1], [, p2]) => p1 - p2);
  let [, pos] = points[0];
  let ans = 1;
  for (let [b0, b1] of points) {
    if (b0 > pos) {
      pos = b1;
      ans++;
    }
  }
  return ans;
}
