function findContentChildren(g: number[], s: number[]): number {
  const sort = (a: number, b: number) => a - b;
  g.sort(sort);
  s.sort(sort);
  const gLen = g.length;
  const sLen = s.length;
  let gI = 0;
  let sI = 0;
  let ans = 0;
  while (gI < gLen) {
    if (sI === sLen) return ans;
    if (g[gI] <= s[sI]) {
      gI++;
      sI++;
      ans++;
    } else {
      sI++;
    }
  }
  return ans;
}
