function rand10(): number {
  let row: number, col: number, idx: number;
  do {
    row = rand7();
    col = rand7();
    idx = col + (row - 1) * 7;
  } while (idx > 40);
  return 1 + ((idx - 1) % 10);
}

function rand10(): number {
  let num!: number;
  do num = (rand7() - 1) * 7 + rand7();
  while (num > 40);
  return (num % 10) + 1;
}
