function findComplement(num: number): number {
  const bitStr = num
    .toString(2)
    .split('')
    .map(v => (v === '1' ? '0' : '1'))
    .join('');
  return parseInt(bitStr, 2);
}

function findComplement(num: number): number {
  let bit = 0;
  for (; bit <= 30; bit++) if (num < 1 << bit) break;
  if (bit === 31) bit = 0x7fffffff;
  bit = (1 << bit) - 1;
  return bit ^ num;
}
